$j('.BrightonCrm').on('submit',function(event) {
  event.preventDefault();
  form = $j(this);
  postdata = $j(form).serializeArray();
  newArrivalApi(postdata);
});

function newArrivalApi(formdata){
  // get the form data

  // process the form
  $j.ajax({
    type      : 'POST',
    url       : AjaxBaseUrl + 'Shop/Customer/BrightonCrm',
    data      : formdata,
    dataType  : 'json',
    encode    : true
  })

  // using the done promise callback
  .done(function(data) {
    // stop the form from submitting the normal way and refreshing the page
    event.preventDefault();
    console.log(data);

    //if(data['subscriberKey'] != ''){
    if(data['error']){
		//Error
		$j('#form-one').addClass('has-error');
		$j('#welcome_email_error_message').html('Please enter a valid email address');
		console.log(data['error']);
		suggestedemail = data['error'].split("Did you mean: ")[1];
		if (data['error'] == "Undeliverable email address") {
			$j('#form-one label').addClass('sr-only');
		} else if (suggestedemail.length > 1) {
			$j("#welcome_email_value").val(suggestedemail);
			$j("#welcome_email_error_label").html('Did you mean?').removeClass('sr-only');
		} else {
			$j('#form-one label').addClass('sr-only');
		}
    }
    else{
      //Success
	  $j('#welcomeBr .c_modal_close').click()
	  $j('#form-one').removeClass('has-error');
	  $j('#form-one label').text('Enter Email Address').addClass('sr-only');
	  $j('#welcome_email_error_message').html('');
      console.log('success!');
    }
  });
}

