var suggestedSearchTerms = new Bloodhound({
	datumTokenizer: Bloodhound.tokenizers.obj.whitespace,
	queryTokenizer: Bloodhound.tokenizers.whitespace,
	remote: {
		url             : 'https://brm-suggest-0.brsrvr.com/api/v1/suggest/?account_id=5334&rsp_fmt=v2&auth_key=&domain_key=brighton_com&request_id='+Math.floor(Math.random()*1E12)+'&url=https%3A%2F%2Fwww.brighton.com%2F&q=%QUERY&request_type=suggest',
		wildcard        : '%QUERY',
		transform: function(result) {
			output = [];
			$j.each(result.response.suggestions, function( index, value ) {
				output.push(value.dq);
			});
            return output;
        }
  }
});

