function isIE() {
	var myNav = navigator.userAgent.toLowerCase();
	return (myNav.indexOf('msie') != -1) ? parseInt(myNav.split('msie')[1]) : false;
};

var videReady = function() {
	if ($j('.mm-page').length) {
		console.log('PAGE ready')
		//
		//
		
			var o = $j('.lazy-img img');
			if (o.length > 0) {
				IncludeJavaScript("//www.brighton.com/store_image/site/js/jquery.unveil.js",function() {
					var o = $j('.lazy-img img');
					//$j(document).ready(function () {
						$j(o).unveil(0, function () {
							if (isIE() && isIE() < 9) {
								$j(this).load().addClass("lazy-loaded");
							} else {
								$j(this).load(function () {
									$j(this).addClass("lazy-loaded");
								})
							}
						});
					//});
					//
					//$j(window).load(function () {
						$j(window).trigger('lookup.unveil');
					//});
				});
			}
		
		//
		//
		var o = $j('.vide');
		if (o.length > 0) {
			IncludeJavaScript("//www.brighton.com/store_image/site/js/jquery.vide.js",function() {
			});
		}
		//
		//
		
		//
		//
		IncludeJavaScript("//www.brighton.com/store_image/site/js/device.min.js",function() {
			//
			//
			var o = $j('html');
			if (o.hasClass('desktop')) {
				IncludeJavaScript("//www.brighton.com/store_image/site/js/jquery.ui.totop2.js",function() {
					$j().UItoTop({easingType: 'easeOutQuart'});
				});
			}
			//
			//
			(function ($j) {
				var o = $j('.parallax');
				if (o.length > 0 && $j('html').hasClass('desktop')) {
					//IncludeJavaScript("//www.brighton.com/store_image/site/js/jquery.rd-parallax.js",function() {
					//});
					(function ($) {
						var $window = $j(window);
						var windowHeight = $window.height();

						$window.resize(function () {
							windowHeight = $window.height();
						});

						$.fn.parallax = function (xpos, speed, offset, outerHeight) {
							var $this = $j(this);
							var getHeight;
							var speedFactor = speed;
							var offset = offset ? parseInt(offset) : null;



							if (outerHeight) {
								getHeight = function (jqo) {
									return jqo.outerHeight(true);
								};
							} else {
								getHeight = function (jqo) {
									return jqo.height();
								};
							}

							if (arguments.length < 1 || xpos === null) xpos = "50%";
							if (arguments.length < 2 || speedFactor === null) speedFactor = -0.2;
							if (arguments.length < 3 || offset === null) offset = 0;
							if (arguments.length < 4 || outerHeight === null) outerHeight = true;

							function update() {

								var pos = $window.scrollTop();



								$this.each(function () {
									var $element = $j(this);
									var top = $element.offset().top;
									var height = getHeight($element);
									if (top + height < pos || top > pos + windowHeight) {
										return;
									}

									$element.css('backgroundPosition', xpos + " " + (Math.round((top - pos) * speedFactor) + offset) + "px");
								});
							}

							$window.bind('scroll', update).resize(update);
							update();
						};
					})(jQuery);

					//$j(document).ready(function () {
						var obj;
						if ((obj = $j('.parallax')).length > 0 && $j('html').hasClass('desktop')) {
							for (var i = 0; i < obj.length; i++) {
								if(isIE() && isIE() < 9){
									$j(obj[i]).parallax($j(obj[i]).css('backgroundPositionX'), obj[i].getAttribute('data-parallax-speed'), obj[i].getAttribute('data-parallax-offset'));
								}else{
									$j(obj[i]).parallax($j(obj[i]).css('background-position').split(" ")[0], obj[i].getAttribute('data-parallax-speed'), obj[i].getAttribute('data-parallax-offset'));
								}
							}
						}

						function isIE() {
							var myNav = navigator.userAgent.toLowerCase();
							return (myNav.indexOf('msie') != -1) ? parseInt(myNav.split('msie')[1]) : false;
						};
					//});
				}
			})(jQuery);
		});
		//
		//
		clearInterval(videWait);
	} else {
		console.log('PAGE not ready')
	}
};

