            suggestedemail = data['error'].split("Did you mean: ")[1];
               if(suggestedemail.length > 13){
                  $j("#email_address_24").val(suggestedemail);
                    $j("#email_address_24_label").html('Did you mean?');
               }
				
			}
			else{
				//Success
				console.log('success!');
				console.log(data);
				$j("#confirm").removeClass('error');
				$j("#email_address_24_label").html('Subscribe to Brighton.com!');
                               $j('#email_address_24_error').html('');
				checksubscription();
			}
		});
	}

