var options = {
    width: "65rem",
    height: "63rem",
    pdfOpenParams: { view: 'FitV'}
};
         PDFObject.embed("http://www.henryjohnsoncs.org/wp-content/uploads/2019/03/Serenity-Porter-3A.pdf", "#example1", options);
         PDFObject.embed("http://www.henryjohnsoncs.org/wp-content/uploads/2019/03/Raevel-Johnson-4C.pdf", "#example2", options);
         PDFObject.embed("http://www.henryjohnsoncs.org/wp-content/uploads/2019/03/Kanylah-Myers-3rd.pdf", "#example3", options);

function initialize() {
  var mapOptions = {
	zoom: 15,
	scrollwheel: false,
	center: new google.maps.LatLng(42.675922,-73.783311)
  };

  var map = new google.maps.Map(document.getElementById('googleMap'),
	  mapOptions);


  var marker = new google.maps.Marker({
	position: map.getCenter(),
	animation:google.maps.Animation.BOUNCE,
	icon: 'http://www.henryjohnsoncs.org/wp-content/themes/hjcs/img/map-marker.png',
	map: map
  });

}

google.maps.event.addDomListener(window, 'load', initialize);
