function resizeSlider_ir4667() {

	var windowWidth = parseFloat(document.body.offsetWidth);
	var containerWidth = windowWidth;

	var contentWidth = parseFloat("1000");
	if(contentWidth==0) {
		contentWidth = containerWidth;
	}

	if(windowWidth<contentWidth) {
		containerWidth = contentWidth;
	}

	document.getElementById("ir4667-ir4667-slider").style.width = containerWidth + "px";
	var fadeSlice = document.getElementById("nivo-slice-0");
	if(fadeSlice!=null) {
		fadeSlice.style.width = containerWidth + "px";
	}
	// document.getElementById("ir4667-ir4667-slider").style.height = Math.max((parseFloat("454")/parseFloat("348") * containerWidth), parseFloat("454")) + "px";
	document.getElementById("ir4667-ir4667-slider").style.marginLeft = ((contentWidth - containerWidth) / 2.0) + "px";

}

