	var $upcomingEventsSlider;
	$(document).ready(function () {
	//$(window).load(function() {
		$upcomingEventsSlider = $('#upcomingEventsSlider').bxSlider({
			prevSelector: '#upcomingEventsSliderPrev',
			nextSelector: '#upcomingEventsSliderNext',

			minSlides: 6,
			maxSlides: 6,
			slideMargin: 10,
			adaptiveHeight: true,
			pager: true,
			controls: true,
			mode: 'vertical',

			prevText: '',
			nextText: '',

			onSliderLoad: function () {
				var $prevSelector = $('#upcomingEventsSliderPrev');
				var $nextSelector = $('#upcomingEventsSliderNext');

				$prevSelector.detach().css('display', 'inline').prependTo('.bx-pager.bx-default-pager');
				$nextSelector.detach().css('display', 'inline').appendTo('.bx-pager.bx-default-pager');
			},

			//On resize, need to re-wire up because we lose the selectors
			onSliderResize: function () {
				var $newPrevSelector = $('').html('');
				var $newNextSelector = $('').html('');
				$('#upcomingEventsSliderPrev').remove();
				$('#upcomingEventsSliderNext').remove();

				$newPrevSelector.prependTo('.bx-pager.bx-default-pager');
				$newNextSelector.appendTo('.bx-pager.bx-default-pager');

				$newPrevSelector.click(function (e) {
					e.preventDefault();
					var current = $upcomingEventsSlider.getCurrentSlide();
					$upcomingEventsSlider.goToPrevSlide(current) - 1;
				});
				$newNextSelector.click(function (e) {
					e.preventDefault();
					var current = $upcomingEventsSlider.getCurrentSlide();
					$upcomingEventsSlider.goToNextSlide(current) + 1;
				});
			}
		});
	});

