$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=Z8bMf7KppypoFJPQ2iaR3w%3d%3d&SessionID=7cf8904e-7b2d-47d7-a070-20406eb26d94&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f19%2f2020+4%3a01%3a36+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.23.222';
