function resizeSlider_ir5476() {

	var windowWidth = parseFloat(document.body.offsetWidth);
	var containerWidth = windowWidth;

	var contentWidth = parseFloat("1080");
	if(contentWidth==0) {
		contentWidth = containerWidth;
	}

	if(windowWidth<contentWidth) {
		containerWidth = contentWidth;
	}

	document.getElementById("ir5476-ir5476-slider").style.width = containerWidth + "px";
	var fadeSlice = document.getElementById("nivo-slice-0");
	if(fadeSlice!=null) {
		fadeSlice.style.width = containerWidth + "px";
	}
	// document.getElementById("ir5476-ir5476-slider").style.height = Math.max((parseFloat("683")/parseFloat("1900") * containerWidth), parseFloat("683")) + "px";
	document.getElementById("ir5476-ir5476-slider").style.marginLeft = ((contentWidth - containerWidth) / 2.0) + "px";

}

if(window.addEventListener) {
	window.addEventListener("resize", function() { resizeSlider_ir5476(); }, false);
} else if(window.attachEvent) {
	window.attachEvent("onresize", function() { resizeSlider_ir5476(); });
}
