div.script:
        var $timer = $("#lblTimeoutTimer");
            function update() {
                var myTime = $timer.html();
                var ss = myTime.split(":");
                var dt = new Date();
                dt.setHours(0);
                dt.setMinutes(ss[0]);
                dt.setSeconds(ss[1]);

                var dt2 = new Date(dt.valueOf() - 1000);
                var temp = dt2.toTimeString().split(" ");
                var ts = temp[0].split(":");

                $timer.html(ts[1] + ":" + ts[2]);

                if ((ts[1] === "02") && (ts[2] === "00")) {
                    //modal alert
                    bootbox.confirm({
                        title: "Your session is about to expire due to inactivity",
                        message: "Would you like to continue your session?",
                        buttons: {
                            confirm: {
                                label: 'Yes',
                                className: 'btn-success'
                            },
                            cancel: {
                                label: 'No',
                                className: 'btn-danger'
                            }
                        },
                        callback: function (result) {
                            console.log('This was logged in the callback: ' + result);
                            if (result) location.reload();
                            else $('a[name="ucLiquidHeader_btnLogout"]')[0].click();
                        }
                    });
                }
                if ((ts[1] === "00") && (ts[2] === "00")) {
                    $('a[name="ucLiquidHeader_btnLogout"]')[0].click();
                }
                else { setTimeout(update, 1000); }
            }

link.script:
    $(document).ready(function () {

        // Search Panel
        const searchPanel = $("#ucHeader_pnlSearch");
        const searchToggle = $("#search-toggle");
        const searchClose = $("#search-close");
        const searchBox = $("#ucHeader_txtSearch");

        searchToggle.click(function () {
            searchPanel.addClass("togglebox__visible");
            searchBox.focus();
        });

        searchClose.click(function () {
            searchPanel.removeClass("togglebox__visible");
            searchBox.blur();
        });


        // Quick Links
        const linksButton = $("#links-button");
        const linksButtonIcon = $("#links-button-icon");
        const headerLinks = $("#header-links");

        linksButton.on("click", function(){
            linksButton.toggleClass("header-links--button__open");
            headerLinks.toggleClass("header-links__open");
        });

        $(document).click(function(event){
            
            var target = $(event.target);

            if(!target.closest('.header-links--outer').length){
                linksButton.removeClass("header-links--button__open");
                headerLinks.removeClass("header-links__open");
            }
        });
    });

    $('#navigation').slicknav({
