var downHover = false;
var downTimer = 0;
var downMaxTimer = 60;
var downDelay = 150;
$(document).ready(function () {
    $(".DownContainer a").mouseover(function () {
        downHover = true;
    });
    $(".DownContainer a").mouseout(function () {
        downHover = false;
    });
});
setInterval(function () {
    if (downHover || downTimer > 0) {
        downTimer++;
        var w = 50 + (50 * downTimer / downMaxTimer);
        var h = 50 + (50 * downTimer / downMaxTimer);
        var o = (((downMaxTimer) - (downTimer)) / downMaxTimer);
        $("#pulse").css("display", "block");
        $("#pulse").css("width", w + "px");
        $("#pulse").css("height", h + "px");
        $("#pulse").css("left", -w / 2 + "px");
        $("#pulse").css("top", (-h / 2) + 25 + "px");
        $("#pulse").css("opacity", o);
        if (downTimer >= downMaxTimer) {
            $("#pulse").css("display", "");
            $("#pulse").css("width", "");
            $("#pulse").css("height", "");
            $("#pulse").css("left", "");
            $("#pulse").css("top", "");
            $("#pulse").css("opacity", "");
        }
        if (downTimer >= downMaxTimer + downDelay) {
            downTimer = 0;
            $("#pulse").css("display", "");
            $("#pulse").css("width", "");
            $("#pulse").css("height", "");
            $("#pulse").css("left", "");
            $("#pulse").css("top", "");
            $("#pulse").css("opacity", "");
        }
    }
}, 1);

