    jQuery(document).ready(function ($) {

        $(".megamenucss115 #CSS3-megaMenuToggle").click(function () {
             // Menu on mobile
                if(document.body.offsetWidth < 769){ 0
                                
                    if ((!$('.jux-custom-css').hasClass('jux-custom-css'))) {
                        jQuery('.mainNav').navAccordion({
                            expandButtonText: '',  //Text inside of buttons can be HTML
                            collapseButtonText: ''
                        });

                    }
                }
                // end menu on mobile
            $(".megamenucss115 .js-megamenu").toggleClass("dropdown-menucssschools");
            $('.jux-custom-css').addClass('accordion-btn-wrap');
        });
        if(document.body.offsetWidth < 769){
            $(".megamenucss115 .jux_tabs_db").removeClass("jux_tabs_item");  
        }
        $(window).resize(function () {

            if (document.body.offsetWidth > 768) {
                $(".megamenucss115 .jux_tabs_db").addClass("jux_tabs_item"); 
                $(".megamenucss115 .js-megamenu").removeClass("dropdown-menucssschools"); 
                $('.jux-custom-css').removeClass('accordion-btn-wrap');
            }else{
                $(".megamenucss115 .jux_tabs_db").removeClass("jux_tabs_item");
            }

        });
    });

