    const slideshowSlides = $(".homepage-slideshow--slide");
    const siteHeader = $(".site-header");
    const slideshowTabs = $(".slideshow-tabs");

    $(window).resize(function(){
        var height = window.innerHeight;
        height *= 0.85;
        height -= siteHeader.outerHeight();
            
        var tabHeight = slideshowTabs.outerHeight();

        var ratioHeight = window.innerWidth * 0.5625;

        if(tabHeight < height && tabHeight < ratioHeight){
            slideshowSlides.css("max-height", height + "px");
            slideshowSlides.css("height", "");
        }
        else {
            slideshowSlides.css("max-height", "");
            slideshowSlides.css("height", tabHeight + "px");
        }
    }).resize();

    function setButtonHeight(){
        var width = $(".hp-button").outerWidth();
        var height = width * 0.75;
        $(".hp-button").css("height", height + "px");
    }

    var tout = null;

    $(window).resize(function(){
        clearTimeout(tout);
        tout = setTimeout(function(){
            setButtonHeight();
        }, 20);
    });

    setButtonHeight();

    $(document).ready(function () {
        // Liquid Button
        const liquidButton = $("#ucFooter_hlLiquidButton");
        const liquidHeader = $(".liquid-header");
        const body = $("body");

        liquidButton.click(function () {
            
            liquidButton.toggleClass("liquid-button__open");

            body.addClass("body__transition-liquid");

            if(liquidButton.hasClass("liquid-button__open")){
                body.addClass("body__liquid-open");
                liquidHeader.addClass("liquid-header__open");
            }
            else {
                body.removeClass("body__liquid-open");
                liquidHeader.removeClass("liquid-header__open");
            }

            setTimeout(function(){
                body.removeClass("body__transition-liquid");
            }, 300);
        });
    });
