ENCountdown = function(div, title, message, year, month, day, hour, minute, second) {

	var _months = new Array("Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec");

	var _futureString = _months[month - 1] + " " + day + ", " + year + " " + hour + ":" + minute + ":" + second + " GMT";

	this.countdown = function() {
		var today = new Date();

		var curYear = today.getYear();
		if (curYear < 1000)
			curYear += 1900;
		var curMonth = today.getMonth();
		var curDay = today.getDate();
		var curHour = today.getHours();
		var curMinute = today.getMinutes();
		var curSecond = today.getSeconds();

		var curString = _months[curMonth] + " " + curDay + ", " + curYear + " " + curHour + ":" + curMinute + ":" + curSecond + " GMT";	 

		var difference = Date.parse(_futureString) - Date.parse(curString);
		var dayDifference = Math.floor(difference/(60*60*1000*24)*1);
		var hourDifference = Math.floor((difference%(60*60*1000*24))/(60*60*1000)*1);
		var minuteDifference = Math.floor(((difference%(60*60*1000*24))%(60*60*1000))/(60*1000)*1);
		var secondDifference = Math.floor((((difference%(60*60*1000*24))%(60*60*1000))%(60*1000))/1000*1);

		if((dayDifference <= 0 && hourDifference <= 0 && minuteDifference <= 0 && secondDifference <= 1 && curDay == day) || dayDifference <= -1) {
			div.innerHTML = message;
		} else {
			document.getElementById('enCountdownDiv').innerHTML =  dayDifference + (dayDifference == 1 ? "
 day":"
 days"); 
			document.getElementById('enCountdownDiv2').innerHTML = hourDifference + (hourDifference == 1 ? "
 day":"
 hours") ; 
			document.getElementById('enCountdownDiv3').innerHTML = minuteDifference + (minuteDifference == 1 ? " 
 day":"
 minutes"); 
			document.getElementById('enCountdownDiv4').innerHTML = secondDifference + (secondDifference == 1 ? " 
 day":"
 seconds");
		}
	}
}

var _enCountdown = new ENCountdown(document.getElementById("enCountdownDiv"), "WHS Graduation Countdown", "Congratulations Class of 2020!!", 2020, 5, 22, 18, 30, 0);

function _doCountdown() {
	_enCountdown.countdown()
	setTimeout("_doCountdown()", 1000);
}

_doCountdown();

