var SlideShow8370260initilized = false;
var SlideShow8370260 = new function() { };
LCMS.jq.extend(SlideShow8370260, slideShowClass);
//initialize the containers to that hold the previous, current and next images.
SlideShow8370260.autoTransitionWait = 5;
SlideShow8370260.container = LCMS.jq("#sidebar_4_4_ctl00_SlideShowImageContent");
SlideShow8370260.currentContentContainer = LCMS.jq("#sidebar_4_4_ctl00_current");
SlideShow8370260.nextContentContainer = LCMS.jq("#sidebar_4_4_ctl00_next");
SlideShow8370260.previousContentContainer = LCMS.jq("#sidebar_4_4_ctl00_previous");
SlideShow8370260.selectedContentContainer = LCMS.jq("#sidebar_4_4_ctl00_selected");
//initialization function to set up the slideshow.  Once initialized the js will start the timer.

//set the height and the width of the container that contains the image and the caption so that the caption appears attached to the image
SlideShow8370260.resizeCaptionContainer = function() {
    var $img = LCMS.jq(this);
	$img.parents('.captionContainer').eq(0).width($img[0].width).height($img[0].height);
};

SlideShow8370260.setContainers = function(current_container) {
	//set up the containers for the next transition
	if (current_container === undefined) current_container = 'index';
	
	switch (current_container) {
		case 'next':
			SlideShow8370260.currentContentContainer.html(SlideShow8370260.nextContentContainer.html());
		break;
		case 'prev':
			SlideShow8370260.currentContentContainer.html(SlideShow8370260.previousContentContainer.html());
		break;
        case 'selected':
			SlideShow8370260.currentContentContainer.html(SlideShow8370260.selectedContentContainer.html());
		break;
		default:
			SlideShow8370260.currentContentContainer.html(SlideShow8370260.contents[SlideShow8370260.currentContentIndex]);
	}
    SlideShow8370260.nextContentContainer.html(SlideShow8370260.contents[SlideShow8370260.NextContentIndex(SlideShow8370260)]);
    SlideShow8370260.previousContentContainer.html(SlideShow8370260.contents[SlideShow8370260.PreviousContentIndex(SlideShow8370260)]);
};

SlideShow8370260.updateIndexes = function () {
    var currentIndex = SlideShow8370260.currentContentIndex;
    var photoCount = SlideShow8370260.container.parent().find('#SlideShow8370260IndexNavigation').data('photo-count');

    SlideShow8370260.container.parent().find('.sldw_photo_index.selected').removeClass('selected');
    SlideShow8370260.container.parent().find('.sldw_photo_index[data-index=' + currentIndex + ']').addClass('selected');
   
    if (photoCount > 7) {
        var maxIndex = SlideShow8370260.CalcMaxIndex(photoCount);
        var minIndex = SlideShow8370260.CalcMinIndex(photoCount);
        SlideShow8370260.container.parent().find('.sldw_photo_index').each(function() {
            var $li = LCMS.jq(this);
            var photoIndex = parseInt($li.data('index'));
            if ((photoIndex < minIndex) || (photoIndex > maxIndex)) {
                $li.hide();
            }
            else {
                $li.show();
            }
            
            if (photoIndex === maxIndex && maxIndex !== (photoCount - 1)) {
                $li.find('a').text('...');
            }
            else if (photoIndex === minIndex && minIndex !== 0) {
                $li.find('a').text('...');
            }
            else {
                $li.find('a').text((photoIndex + 1));
            }
        });
    }
};

SlideShow8370260.InitializeTransition = function()
{
    if (SlideShow8370260initilized) return;
    SlideShow8370260initilized = true;
	//load the containers based on the currentContentIndex
    SlideShow8370260.setContainers();
    
    SlideShow8370260.container.append(SlideShow8370260.currentContentContainer.html());
    
    
    	
    	var current_caption_container = SlideShow8370260.container.children('.SlideShowImage').first().children('.captionContainer')

        //set the captionbox to resize on the image load event
        if (current_caption_container.children('img').length > 0) {
			current_caption_container.children('img').unbind('load.LCMSslideshow').bind('load.LCMSslideshow', SlideShow8370260.resizeCaptionContainer);
        } else {
        	current_caption_container.children().children('img').unbind('load.LCMSslideshow').bind('load.LCMSslideshow', SlideShow8370260.resizeCaptionContainer);
        }
}

// Perform transition is run after the timer expires or when the next/prev arrows are clicke
SlideShow8370260.PerformTransition = function(trans_dir) 
{
	if (SlideShow8370260.transitioning == true) return;    
    var is_fwd = (trans_dir === 'next');
    var is_selected_index = (trans_dir === 'selected');
    var next_img = '';

    if (!is_selected_index) {
        // next_img only references the correct element before setContainers is called!
        next_img = is_fwd ? SlideShow8370260.nextContentContainer : SlideShow8370260.previousContentContainer;
    } else {
        SlideShow8370260.selectedContentContainer.html(SlideShow8370260.contents[SlideShow8370260.currentContentIndex]);
        next_img = SlideShow8370260.selectedContentContainer;
    }

    var next_caption_container = next_img.find('.captionContainer');
	if (next_caption_container.children('img').length > 0) {
		next_caption_container.children('img').unbind('load.LCMSslideshow').bind('load.LCMSslideshow', SlideShow8370260.resizeCaptionContainer);
	} else {
		next_caption_container.children().children('img').unbind('load.LCMSslideshow').bind('load.LCMSslideshow', SlideShow8370260.resizeCaptionContainer);
	}

    SlideShow8370260.updateIndexes();
    
    
    
    
        next_img.css('display', 'block');
        if (next_img.children('.SlideShowImage').eq(0).children().children('img').length > 0) {
			next_img.children('.SlideShowImage').eq(0).children().children('img').first().each(SlideShow8370260.resizeCaptionContainer);
        } else {
        	next_img.children('.SlideShowImage').eq(0).children().children().children('img').first().each(SlideShow8370260.resizeCaptionContainer); 
        }


        
            
            var start = {};
            var end = {};
            

            //hide the current container
	        next_img.css('display', 'none');
	        var animateDiv = SlideShow8370260.container.children('.SlideShowImage').eq(0);
            animateDiv.hide('fade', start, 0.8 * 500, function() {                
                animateDiv.html(next_img.children(":first").clone(true));
	            next_img.css('display', 'block');
	            SlideShow8370260.container.parent().parent().height(next_img.height());
	            next_img.css('display', 'none');
	            SlideShow8370260.setContainers(trans_dir);
                animateDiv.show('fade', end, 0.8 * 500);
            });

        
}

function nextSlide8370260() {
	SlideShow8370260.MoveToNextSlide(SlideShow8370260, 'SlideShow8370260');
}
function previousSlide8370260() {
	SlideShow8370260.MoveToPreviousSlide(SlideShow8370260, 'SlideShow8370260');
}
function goToSlide8370260(index) {
    SlideShow8370260.MoveToSlide(SlideShow8370260, index);
}

SlideShow8370260.SetContents = function() {
    var imageContainer = LCMS.jq("#sidebar_4_4_ctl00_divImages");
    var containerWidth = imageContainer.width();
    imageContainer.css("width", containerWidth + "px");
    
	SlideShow8370260.contents = [];

        //piece together the slideshowimage div based on properties set in the photo gallery settings
        //SlideShowImage is the base of all images
        var content = '<div class="SlideShowImage"><div class="captionContainer"';
        //if we have the properties from the settings we pass them in and set up the resizer
        
        content = content + ">";
        //code behind returns "" if the link is null or empty, otherwise it creates the anchor tag
        content = content + "";
        //base for the image tag
        content = content + "<img src=";
        //if we have the properties from the settings we pass them in and set up the resizer
         
        content = content + "\"/ResizeImage.aspx?m=" + containerWidth + "&img=%2fWebsites%2fbpschools%2fPhotoGallery%2f6586553%2fScreen+Shot+2020-03-22+at+4.09.33+PM.png&55603\""; 
         
        content = content + " alt=\"Screen Shot 2020-03-22 at 4.09.33 PM\" />";
        //code behind returns "" if the link is null or empty, otherwise it creates the closing anchor tag
        content = content + "";
        content = content + "";
        //if we have a caption the codebehind returns the div for the caption otherwise it returns ""
        
            //if we have a caption the codebehind returns the div for the caption otherwise it returns ""
        
        content = content + "</div></div>";
        SlideShow8370260.contents.push(content);
    
        //piece together the slideshowimage div based on properties set in the photo gallery settings
        //SlideShowImage is the base of all images
        var content = '<div class="SlideShowImage"><div class="captionContainer"';
        //if we have the properties from the settings we pass them in and set up the resizer
        
        content = content + ">";
        //code behind returns "" if the link is null or empty, otherwise it creates the anchor tag
        content = content + "";
        //base for the image tag
        content = content + "<img src=";
        //if we have the properties from the settings we pass them in and set up the resizer
         
        content = content + "\"/ResizeImage.aspx?m=" + containerWidth + "&img=%2fWebsites%2fbpschools%2fPhotoGallery%2f6586553%2fScreen+Shot+2020-03-22+at+4.09.06+PM.png&55603\""; 
         
        content = content + " alt=\"Screen Shot 2020-03-22 at 4.09.06 PM\" />";
        //code behind returns "" if the link is null or empty, otherwise it creates the closing anchor tag
        content = content + "";
        content = content + "";
        //if we have a caption the codebehind returns the div for the caption otherwise it returns ""
        
            //if we have a caption the codebehind returns the div for the caption otherwise it returns ""
        
        content = content + "</div></div>";
        SlideShow8370260.contents.push(content);
    
}
var SlideShow8370260Started = false;
LCMS.jq(function() {
    if (SlideShow8370260Started)
        return;
    SlideShow8370260Started = true;
	SlideShow8370260.SetContents(); 
	SlideShow8370260.StartSlideShow(SlideShow8370260, 'SlideShow8370260');
    LCMS.jq("#sidebar_4_4_ctl00_divImagesMain")
        .mouseenter(function() {
		    LCMS.jq('#SlideShow8370260Navigation').css('visibility', 'visible');
		    SlideShow8370260.StopSlideShow(SlideShow8370260)
	    })
        .mouseleave(function() {
		    LCMS.jq('#SlideShow8370260Navigation').css('visibility', 'hidden');
		    SlideShow8370260.ResumeSlideShow(SlideShow8370260, 'SlideShow8370260');
	    });
	
		
	
       		var resize_timer = null;
       		
			LCMS.jq(window).bind('resize', function() {
				if (!resize_timer) { // timer to throttle how many times this fires on resize
					resize_timer = setTimeout(function() {
						
						var findCurrentMsize = function(img_src){
							var start_str = img_src.indexOf('?m='), first_amp = img_src.indexOf("&");
							return img_src.substring(start_str + 3, first_amp);
						};
						
						var	resizeM, 
							container_width = LCMS.jq('#sidebar_4_4_ctl00_divImages').parent().width(),
							current_m_val = findCurrentMsize(LCMS.jq('.captionContainer img', SlideShow8370260.currentContentContainer).first().attr('src')) - 0; // - 0 to convert to number
						
						
						// only resize if our resize width doesn't match the container
						if (current_m_val !== container_width) {
							
							resizeM = function(img_src) {
								var start_str = img_src.indexOf('?m='), first_amp = img_src.indexOf("&");
								var old_str = img_src.substring(start_str, first_amp);
								return img_src.replace(old_str, '?m=' + container_width);
							};
							
							LCMS.jq('#sidebar_4_4_ctl00_divImages').parent('.SlideShowContainer').css("height", "auto");
							LCMS.jq('#sidebar_4_4_ctl00_divImages, #sidebar_4_4_ctl00_divImages .captionContainer').removeAttr('style');
							LCMS.jq('#sidebar_4_4_ctl00_divImages .SlideShowImage').css({'width': 'auto'});
							
							
							// resize the slideshow images currently showing
							LCMS.jq('#sidebar_4_4_ctl00_divImages .captionContainer img').each(function() {
								var $ele = LCMS.jq(this);
								$ele.attr('src', resizeM($ele.prop('src'))).css({display: 'block'}); // set new src
								
								// manually fire load event, this is needed for some browsers that wont fire if the image gets cached. Some browsers will fire the event twice because of this but that's ok.
								if ($ele.prop('complete')) { $ele.trigger('load'); }													
							});
					
					
							// resize everything (in the array)
							LCMS.jq.each(SlideShow8370260.contents, function(indx, img_src) {
								SlideShow8370260.contents.splice(indx, 1, resizeM(img_src));	
							});

							
							LCMS.jq('#sidebar_4_4_ctl00_divImages, #sidebar_4_4_ctl00_divImages .SlideShowImage').css({'width': container_width});
							

							
						}

						resize_timer = null;				
					}, 300);	
				}				
			});        
    
});
