body.script:
function googleTranslateElementInit() {
  new google.translate.TranslateElement({pageLanguage: 'en', layout: google.translate.TranslateElement.InlineLayout.HORIZONTAL, gaTrack: true, gaId: 'UA-90913821-1'}, 'google_translate_element');
}

div.script:
$(window).load(function() {
    $('#slider').orbit({
    advancespeed: 9999,
    timer: 'true',
    bullets: 'true',       // true or false to activate the bullet navigation
    bulletThumbs: 'true',    // thumbnails for the bullets
    bulletThumbLocation: '../images/orbit-demo/bullets/',    // location from this file where thumbs will be
    });
});

html.script:
function googleTranslateElementInit() {
  new google.translate.TranslateElement({pageLanguage: 'en', layout: google.translate.TranslateElement.FloatPosition.TOP_LEFT, gaTrack: true, gaId: 'UA-71546339-1'}, 'google_translate_element');
}
