body.script:
window.onload = function() {
  var redirectUrl = 'https:\x2F\x2Fwww.google.com\x2Faccounts\x2FAccountChooser?hd=perrydale.k12.or.us\x26continue=https%3A%2F%2Fsites.google.com%2Fa%2Fperrydale.k12.or.us%2Fsites%2Fsystem%2Ferrors%2FProxiedWebspaceNotFound%3Fhost%3Dwww.perrydale.k12.or.us%26webspace%3Dperrydale-website\x26followup=https%3A%2F%2Fsites.google.com%2Fa%2Fperrydale.k12.or.us%2Fsites%2Fsystem%2Ferrors%2FProxiedWebspaceNotFound%3Fhost%3Dwww.perrydale.k12.or.us%26webspace%3Dperrydale-website\x26service=jotspot\x26ul=1';
  var domain = 'perrydale.k12.or.us';
  var hash = window.location.hash;
  if (hash) {
  var match = hash.match(/[#&]Email=([^&]+)/);
  if (match) {
  redirectUrl += "&Email=" + match[1] + "@" + domain;
  }
  }
  window.location.replace(redirectUrl);
};
