div.script:
$(document).ready(function () {
    $("#3e7c0420-41a1-48f6-98ed-c3e0ca868fa0").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 10 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#33b02aec-5704-4cf0-b759-a115b73d045d").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#33b02aec-5704-4cf0-b759-a115b73d045d').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#33b02aec-5704-4cf0-b759-a115b73d045d').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 5000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 9 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
