        currentPagePath: '/content/schools/ispa/en',
        label: 'News',
        listFrom: 'solr',
        limit: '10',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/news.html',
        viewAllTitle: 'View All',
        uuid: '2f0fc365-66de-4ed4-a72a-9806d63ef8ed',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

