    // Begin code added by Adam 2/21/17 Custom Project ID PR-045096
    function googleBranded(){
    	$("
").prependTo("#gb-custom-mystart div.inline-children");
        loadScript("//translate.google.com/translate_a/element.js?cb=googleTranslateElementInitVertical", function(){});
    }
    function loadScript(url, callback){
        var script = document.createElement('script')
        script.type = 'text/javascript';

        if (script.readyState){  // IE
                script.onreadystatechange = function(){
                        if (script.readyState == 'loaded' || script.readyState == 'complete'){
                                script.onreadystatechange = null;
                                callback();
                        }
                }
        } else {  // Others
                script.onload = function(){
                        callback();
                }
        }

        script.src = url;
        document.getElementsByTagName('head')[0].appendChild(script);
    }

	function googleTranslateElementInitVertical() {
        new google.translate.TranslateElement({pageLanguage: 'en', layout: google.translate.TranslateElement.InlineLayout.VERTICAL}, 'google_translate_element');
    }
    // End code added by Adam 2/21/17 Custom Project ID PR-045096

	function setBullet(bullet){
		if($(bullet).hasClass('expandable')){ //if it has children apply a different bullet
			$(bullet).addClass('icon-font icon-arrow-left');
		}else if( $(bullet).hasClass('collapsible') ){
			$(bullet).addClass('icon-font icon-arrow-down');
		}
	}


