	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			multimediaRotator();
			$('#gb-schools-list').prependTo('#gb-custom-nav-bar');
			$('#gallery-watermark').prependTo('#hp-slideshow');

		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			multimediaRotator();
			appView();
			$('#gb-schools-list').prependTo('#gb-header-links');
			$('#gallery-watermark').prependTo('.rs-gallery-desc');

		} if (getWidth >= 640 && getWidth < 768) { // 640+
			multimediaRotator();
			appView();
			$('#gb-schools-list').prependTo('#gb-header-links');
			$('.hp .ui-widget.app.headlines .ui-widget-header').append($('.ui-widget.app.headlines .header-more'));
			$('.ui-widget.app.upcomingevents .ui-widget-header').append($('.ui-widget.app.upcomingevents .header-more'));

		} if (getWidth >= 480 && getWidth < 640) { // 480+
			appView();
			$('#gb-schools-list').prependTo('#gb-custom-nav-bar');
			$('.hp .ui-widget.app.headlines').append($('.ui-widget.app.headlines .ui-widget-header .header-more'));
			$('.ui-widget.app.upcomingevents').append($('.ui-widget.app.upcomingevents .ui-widget-header .header-more'));
			if( $('#rs-menu-btn span.icon-font').length == 0 ){
				$('#rs-menu-btn span').prepend('');
			}

		} if (getWidth >= 320 && getWidth < 480) { // 320+
			appView();
			$('#gb-schools-list').prependTo('#gb-custom-nav-bar');
			$('.hp .ui-widget.app.headlines').append($('.ui-widget.app.headlines .ui-widget-header .header-more'));
			$('.ui-widget.app.upcomingevents').append($('.ui-widget.app.upcomingevents .ui-widget-header .header-more'));
			if( $('#rs-menu-btn span.icon-font').length == 0 ){
				$('#rs-menu-btn span').prepend('');
			}
		}
	}

