	function modEvents(){
	    if (!$(".upcomingevents .joel-month").length) {
	        $(".upcomingevents").modEvents({
	            columns     : "yes",
	            monthLong   : "no",
	            dayWeek     : "no"
	        });
	        eventsByDay(".upcomingevents .ui-articles");
	    }
	}

	function eventsByDay(container) {
	    var countLi = 0;
	    $(".ui-article", container).each(function(){
	        if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
	            var moveArticle = $(this).html();
	            $(this).parent().prev().children().children().next().append(moveArticle);
	            $(this).parent().remove();
	        };
	    });
	}

	function appView(){

		//DOC dont add app into accordion if it doesnt have a header
		$("#hp-content .hp.column.one div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}else if( $(".ui-widget-header span.acc-icon", this).length == 0 ){
				$(".ui-widget-header", this).append('');
			}
		});


		//DOC accordion header click
		$("#hp-content .hp.column.one div.ui-widget-header").off('click').click(function(){
			var clickedApp = $(this).parent().parent().attr("id");
			if( $(this).parent().hasClass("active") ){
				$(this).parent().removeClass("active");
				$('span.acc-icon', this).removeClass("icon-minus-thin").addClass("icon-plus-thin");
			}else{
				$("#hp-content .hp.column.one div.ui-widget.app.active").removeClass("active");
				$("#hp-content .hp.column.one div.ui-widget.app.active .ui-widget-header span.acc-icon").removeClass("icon-minus-thin").addClass("icon-plus-thin");
				$(this).parent().addClass("active");
				$('span.acc-icon', this).removeClass("icon-plus-thin").addClass("icon-minus-thin");


				$("html, body").animate({
					scrollTop: $("#" + clickedApp).offset().top - 10
				}, 500, function(){
					$("html, body").stop(true, false);
				});
			}
		});
	}	

