	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        var searchString = "Search...";

	        $("div#custom-search input#swsitesearch-txt-searchstring")
		        .removeAttr("placeholder")
		        .attr("value", searchString)
			.focus(function() {
				if($(this).val() == searchString) {
					$(this).val("");
				}
			})
			.blur(function() {
				if($(this).val() == "") {
					$(this).val(searchString);
				}
			});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
