    /* customDropdowns
	**  Use: Sets up custom dropdown menus.
	**  Param: openIcon, closedIcon -- The classes that you want to mark the on/off state of the menu icons (dont pass the . or #)
	*/
    var openIcon = 'icon-arrow-down';
    var closedIcon = 'icon-arrow-right';
    var dropdownTimeout = ""; //Will hold our timeout object that will get passed among the dropdown functions.
	function customDropdowns(){
    	$('.custom-dropdown input, .custom-dropdown a, .custom-dropdown .custom-dropdown > button').attr('tabindex', -1);

        $('#gb-custom-mystart .custom-dropdown').first().addClass('last-menu-item');

        $('.custom-dropdown button').on('keydown', function(e) { // Trigger the click event from the keyboard
            var code = e.which;
            // 13 = Return, 32 = Space
            if ((code === 13) || (code === 32)) {
                e.preventDefault();

                $(this).click();
                if( !$(this).parent().hasClass('openMenu') ){
                    //clear our keyboard hooks since the menu is closing.
                    dropdownKeyboardFunctionality($('~ .dropdown', this), true);
                }else{
                    //add our keyboard hooks
                    dropdownKeyboardFunctionality($('~ .dropdown', this));
                    $('~ .dropdown a', this).first().focus();
                }
            }else if(code == 39 || code == 40 && $(this).attr('aria-expanded') == 'true'){
                e.preventDefault();
                $('~ .dropdown a', this).first().focus();
            }else if( code == 9 ){
                clearDropdowns();
            }
        });
		//Custom Dropdown Functionality
        $('.custom-dropdown > button').off('click').click(function(e){
            clearTimeout(dropdownTimeout);

            if( $(this).attr("aria-expanded") == "false" ){
                clearDropdowns()
                $('~ .dropdown', this).attr('aria-expanded', 'true').attr('aria-hidden', 'false').show();
                $(this).attr('aria-expanded', 'true');
                $(this).parent().addClass("openMenu");
                $('.dropdown-icon', this).removeClass(closedIcon).addClass(openIcon);
            }else{
                clearDropdowns();
            }
        });
        $('.custom-dropdown').hover(function(e){ //In
            clearTimeout(dropdownTimeout);
        },function(e){ //Out
            //canDropDownHide = true;
            dropdownTimeout = setTimeout(function(){
                clearDropdowns();
            }, 800);
        });
	}
    function dropdownKeyboardFunctionality(dropdown, clear){
        $('a, input, select', dropdown).attr('tabindex', -1).keydown(function(e){
            var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
            var linkObj = $('a, input, select', dropdown);

            switch(e.keyCode) {
                // CONSUME LEFT AND UP ARROWS
                case csKeyCodes.left:
                case csKeyCodes.up:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                        // IS FIRST ITEM
                    if(linkObj.index($(this)) == 0) {
                        $(dropdown).siblings('button').focus();
                    } else {
                        linkObj[linkObj.index($(this)) - 1].focus();
                    }
                break;

                // CONSUME RIGHT AND DOWN ARROWS
                case csKeyCodes.right:
                case csKeyCodes.down:
                    e.preventDefault();
                    e.stopImmediatePropagation();

                    // IS LAST ITEM
                    if(linkObj.index($(this)) == (linkObj.length - 1) ) {
                        // FOCUS FIRST ITEM
                        linkObj[0].focus();
                    } else {
                        // FOCUS NEXT ITEM
                        linkObj[linkObj.index($(this)) + 1].focus();
                    }
                break;

                case csKeyCodes.tab:
                    clearDropdowns();
                break;

                case csKeyCodes.esc:
                    e.preventDefault();
                    $(dropdown).siblings('button').focus();
                    clearDropdowns();
                break;

                default: //pass
                break;
            }
        });
    }
    function clearDropdowns(){
        $('.custom-dropdown input, .custom-dropdown a, .custom-dropdown .custom-dropdown > button').attr('tabindex', -1);
        $('.custom-dropdown.openMenu .dropdown-icon').removeClass(openIcon).addClass(closedIcon);
        $('.custom-dropdown.openMenu .dropdown').attr('aria-expanded', 'false').attr('aria-hidden', 'true').hide();
        $('.custom-dropdown.openMenu > button').removeClass('acc-open').attr('aria-expanded', 'false');
        $('.custom-dropdown.openMenu li').removeClass('active-list-item');
        dropdownKeyboardFunctionality($('.custom-dropdown.openMenu .dropdown'), true);
        $('.custom-dropdown.openMenu').removeClass("openMenu");
	}

	function addAppHeaderIcons(){
		var icons = {
			".hp.column.one .ui-widget-header": "icon-link",
			"#hp-bottom-app .ui-widget-header": "icon-calendar",
			".pagenavigation .ui-widget-header": "icon-na-logo"
		};

		$.each(icons, function(k, v){
			$(k).prepend('');	
		});
	}

	function upcomingEventsApp(){
		if( $('.ui-widget.app.upcomingevents').length > 0){
            $(".ui-widget.app.upcomingevents").each(function() { //added each function by Lyndsay - 1.3.17 - Case 00442734
                $('.ui-widget-detail .view-calendar-link', this).text("View Full Calendar"); //changed .ui-widget.app.upcomingevents to 'this' - 1.3.17 - Case 00442734
                $('.ui-widget-header', this).append($('.ui-widget-detail .view-calendar-link', this).addClass('header-more')); //changed .ui-widget.app.upcomingevents to 'this' - 1.3.17 - Case 00442734

                var events = $('.ui-widget-detail ul.ui-articles li', this); //changed .ui-widget.app.upcomingevents to 'this' - 1.3.17 - Case 00442734
                var eventStruct = '
';
                var firstRunOver = false;
                var eventsHit = 0;
                events.each(function(k, v){
                    if($(this).find('h1').length == 1){
                        if(firstRunOver){
                            eventsHit++;
                            var extraClass = "right-event";
                            if(eventsHit%2 == 0){
                                extraClass = "left-event";
                            }
                            eventStruct += '
';
                        }else{
                            firstRunOver = true;
                        }

                        var day = $(this).find('h1').html().split(",");
                        eventStruct += "
" + day[0] + "";
                        eventStruct += "";
                        eventStruct += "

" + $('.ui-article-description', this).html() + ""; //added by Jeremy - Case #00109501
                    }else{
                        eventStruct += "
" + $(this).html() + ""; //changed .ui-widget.app.upcomingevents to 'this' - 1.3.17 - Case 00442734
                    }
                });
                eventStruct += '';

                $('.ui-widget-detail', this).html(eventStruct); //changed .ui-widget.app.upcomingevents to 'this' - 1.3.17 - Case 00442734
            });
        }
	}

	/*
	** smoothScroll
	** Use: This will smoothly scroll to the section thats id was passed to the function.
	** Return: N/A
	** Parameter(s): String (jQuery "#id") The id of the section to be scolled to.
	*/
	function smoothScroll( to, offset ){
		if( to.indexOf("#") < 0 ){ to = "#" + to; } //makes sure we have a valid id accessor string
		var target = $(to);

		if (target.length) {
			$('html,body').animate({
				scrollTop: target.offset().top - offset //this offset accounts for the fixed menu on the top
			}, 1000, function(){
				$("html, body").stop(true, false);
			});
			return false;
		}
	}

	function showHide(list, comp, menuOption){
		var menuOptionString = "";

		$.each(list, function(k, v){
			if(v[0] === comp){
				$(k).hide();
			}else if(menuOption){
					//build out the menu option string
				menuOptionString += v[1][0] + "," + v[1][1] + "," + v[0] + "," + v[1][2] + ";";
			}
		});

		return menuOptionString;
	}

var randomnumber = Math.floor(Math.random() * (5 - 0 + 1)) + 0;
var randomnumber2 = Math.round(Math.random()*5-1);
var howOften = 15; //number often in seconds to rotate
var current = randomnumber; //start the counter at 0
var ns6 = document.getElementById&&!document.all; //detect netscape 6

var items = new Array()
items[0]="";
items[1]="";
items[2]="";
items[3]="";
items[4]="";
items[5]="";

function rotater() {
    document.getElementById("placeholder").innerHTML = items[current];
    current = (current==items.length-1) ? 0 : current + 1;
    setTimeout("rotater()",howOften*1000);
}

function rotater() {
    if(document.layers) {
        document.placeholderlayer.document.write(items[current]);
        document.placeholderlayer.document.close();
    }
    if(ns6 && document.getElementById("placeholderdiv") != undefined){
    	document.getElementById("placeholderdiv").innerHTML=items[current]
        if(document.all && placeholderdiv){
            placeholderdiv.innerHTML=items[current];
        }
    }
    current = (current==items.length-1) ? 0 : current + 1; //increment or reset
    setTimeout("rotater()",howOften*1000);
}
window.onload=rotater;
