//DISCLAIMER DO NOT CHANGE

this.disclaimerwindow = function(){
                var a = document.getElementsByTagName("a");  
                this.checkURL = function(obj){                       
                                var href = obj.href.toLowerCase();
                                if(href.length < 1){
                                 return false;
                                }
                                for ( var j=0, len=goodDomains.length; j<len; ++j ){
                                                //alert("got here");
                                                //alert(goodDomains[i]);
                                                if (href.indexOf(goodDomains[j])!=-1){
                                                                return false
                                                }
                                }

                                //alert("returned false");
                                return true
                };			
                this.setURL = function(obj){                        //Adds the disclaimer window to all OTHER addresses
                                obj.href = "javascript:oDWin('"+ obj.href +"');";
                                obj.className = "externallink";
                                obj.target= "_self";
								//obj.OnClick= "javascript:oDWin('"+ obj.href +"');return false;";

                };

                // Loop thouigh the anchor collection to find external URL and set modify the attributes
                for (var i=0;i<a.length;i++){
                                if(checkURL(a[i])) setURL(a[i]);
                };                             
};

function oDWin(strurl) {
                var strmessage;
                 strmessage= disclaimerMessage;
		var objInputbox= confirm(strmessage);
                if (objInputbox==true){ 
                               window.open 
                               (strurl,'ExternalWindow','width=800,height=600,resizable=yes,menubar=yes,location=yes,scrollbars=yes,toolbar=yes,status=yes');
                }
}

this.addEvent = function(obj,type,fn){
                if(obj.attachEvent){
                                obj['e'+type+fn] = fn;
                                obj[type+fn] = function(){obj['e'+type+fn](window.event );}
                                obj.attachEvent('on'+type, obj[type+fn]);
                } else {
                                obj.addEventListener(type,fn,false);
                };
};

addEvent(window,"load",disclaimerwindow);

var win=null;
function printIt(printThis)
{
  win = window.open();
  self.focus();
  win.document.open();
  win.document.write('<'+'html'+'><'+'head'+'><'+'style'+'>');
  win.document.write('body, td { font-family: Tahoma; font-size: 10pt;}');
  win.document.write('<'+'/'+'style'+'><'+'/'+'head'+'><'+'body'+'>');
  win.document.write(printThis);
  win.document.write('<'+'/'+'body'+'><'+'/'+'html'+'>');
  win.document.close();
  win.focus();
  var is_chrome = navigator.userAgent.toLowerCase().indexOf('chrome') > -1;
  if (!(is_chrome)){win.print();win.close();}
  if (is_chrome){win.print();}
}

