    function LoadRating1533084() {
    var ModuleInstanceID = 15;
    var FlexDataID = 33084;

        GetContent("https://www.ojrsd.com//site/UserControls/Rating/RatingListWrapper.aspx?ModuleInstanceID=" + ModuleInstanceID + "&FlexDataID=" + FlexDataID + "&IsDisabled=true", "swmoduleinstancerating-pnl-rating1533084", 2);
}

    function InsertRating1533084() {
    var ModuleInstanceID = 15;
    var FlexDataID = 33084;
    var Rating = $('#swmoduleinstancerating-hid-rating1533084').val();
    var data = "{ModuleInstanceID: " + ModuleInstanceID + ", " +
                "FlexDataID: " + FlexDataID + ", " +
                "Rating: " + Rating + "} ";  
    var success = 'LoadRating1533084();';
    var failure = "CallControllerFailure(result[0].errormessage);";

    if (Rating != '' && Rating !== undefined) {
        CallController("https://www.ojrsd.com//site/UserControls/Rating/RatingController.aspx/InsertRating", data, success, failure);
    }
}

    $(document).ready(function() {
        CheckScript('Rating');
        CheckScript('Metadata');

        // initialize rating script
        $("input[name='swmoduleinstanceratinghidrating1533084']").rating({
            callback: function() {
                Getswmoduleinstanceratinghidrating1533084Values();
                InsertRating1533084($('#swmoduleinstancerating-hid-rating1533084').val());
            }
        });
    });
    
    // fill hidden input with value clicked
    function Getswmoduleinstanceratinghidrating1533084Values() {
        var selItem;

        $("input[name='swmoduleinstanceratinghidrating1533084']").each(function(i) {
            if ($(this).is(':checked')) {
                $('#swmoduleinstancerating-hid-rating1533084').val((i + 1) / 2);
            }
        });
    }

