                $('#swmoduleinstancerating-hid-rating1532936').val((i + 1) / 2);
            }
        });
    }

    function LoadRating1530024() {
    var ModuleInstanceID = 15;
    var FlexDataID = 30024;

        GetContent("https://www.ojrsd.com//site/UserControls/Rating/RatingListWrapper.aspx?ModuleInstanceID=" + ModuleInstanceID + "&FlexDataID=" + FlexDataID + "&IsDisabled=true", "swmoduleinstancerating-pnl-rating1530024", 2);
}

    function InsertRating1530024() {
    var ModuleInstanceID = 15;
    var FlexDataID = 30024;
    var Rating = $('#swmoduleinstancerating-hid-rating1530024').val();
    var data = "{ModuleInstanceID: " + ModuleInstanceID + ", " +
                "FlexDataID: " + FlexDataID + ", " +
                "Rating: " + Rating + "} ";  
    var success = 'LoadRating1530024();';
    var failure = "CallControllerFailure(result[0].errormessage);";

    if (Rating != '' && Rating !== undefined) {
        CallController("https://www.ojrsd.com//site/UserControls/Rating/RatingController.aspx/InsertRating", data, success, failure);
    }
}

    $(document).ready(function() {
        CheckScript('Rating');
        CheckScript('Metadata');

        // initialize rating script
        $("input[name='swmoduleinstanceratinghidrating1530024']").rating({
            callback: function() {
                Getswmoduleinstanceratinghidrating1530024Values();
                InsertRating1530024($('#swmoduleinstancerating-hid-rating1530024').val());
            }
        });
    });
    
