$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=0VrLJKtrCrzMngMv6p7ZYw%3d%3d&SessionID=9935ef20-3337-40d2-8f13-fac8b0793981&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f19%2f2020+4%3a58%3a30+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.1.129';
   try {
      $.getJSON(beaconURL + '&jsonp=?', function(myData) {});
   } catch(err) { 
