                                height = 0;
                            }else{
                                height = height;
                            }
                            if (this.hash !== '') {
                                event.preventDefault();
                                var hash = this.hash;
                                $('html, body').animate({
                                  scrollTop: $(hash).offset().top - height
                                }, 500, function(){
                                  window.location.hash = hash;
                                });
                                console.log(height);
                            }

                        });
