var doe=[];
var load_doe1=function(y,m,callback){
  if(y.length!=4||m.length!=2) return;
  $.getJSON(window.location.protocol+'//'+window.location.host+'/calendar/doe/'+y+'/'+m, callback);
}
var proc_doe1=function(y,m,callback){
  var s=y+'-'+m;
  if(doe.indexOf(s)==-1){
    doe.push(s);
    load_doe1(y,m, function(data){
      if(data.code==200){
        for(key in data.response){
          var s=data.response[key].game_date;
          doe.indexOf(s)==-1&&doe.push(s);
        }
      }
      callback();
    });
  }else callback();
}

