				window.addEvent('load', function() {
				var start_index187 = 1;
				start_index187 = start_index187 - 1;
				var current_page = 1;
				var number_page = 1;
				var ajax_url = "http://www.caywood.org/LCSS/modules/mod_ice_accordion/ajax.php?moduleId=187&layout=_items&type=ice_accordion&lang=en-GB";
				var listpage = $("iceaccordion187").getElements(".iceaccordion-paging li");
				
				function addPagingEvent187(){
					if(listpage && number_page > 1){
						listpage.each( function(el, index){
							el.addEvent("click", function( event ){
									event.stop();
									if( (index+1) != current_page){
										getAccordion( index + 1 );
										updateActiveClass( listpage, index );
									}
								});
						});
						if( $("iceaccordion187").getElement(".iceaccordion-prev") ){
							$("iceaccordion187").getElement(".iceaccordion-prev").addEvent( "click", function( event ){
								index = current_page - 1;
								if(index > 0){
									getAccordion( index );
									updateActiveClass( listpage, index -1);
								}
								
							});
						}
						if( $("iceaccordion187").getElement(".iceaccordion-next") ){
							$("iceaccordion187").getElement(".iceaccordion-next").addEvent( "click", function( event ){
								index = current_page + 1;
								if(index <= number_page){
									getAccordion( index );
									updateActiveClass( listpage, index - 1  );
								}
							});
						}
						updateClassArrows( current_page );
					}
					
				}
				function getAccordion( nextpage ){
					updateClassArrows( nextpage );
					var blockHidden = $("iceaccordion187").getElement(".iceaccordion-hidden");
					if( $(blockHidden).getElement(".page-"+nextpage)){
						var htmlHidden = $(blockHidden).getElement(".page-"+nextpage).get("html");
						current_page = nextpage; 
						$("iceaccordion187").getElement(".iceaccordion").set("html", htmlHidden);
						var myAccordion187 = new Accordion( $("iceaccordion187").getElement(".iceaccordion"), '#iceaccordion187 .toggler', '#iceaccordion187 .accordion_content', {
							opacity: false,
							alwaysHide:true,
							display: start_index187,
							onActive: function(toggler, element){
								toggler.addClass('open');
							},
							onBackground: function(toggler, element){
								toggler.removeClass('open');
							}
						});
						imageAccordionPreview( $("iceaccordion187") );
					}
					else{
						$("iceaccordion187").getElement(".iceaccordion-loading").setStyle("display","block");
						var req =new Request({
										  method: 'get',
										  url: ajax_url,
										  data: { 'p' : nextpage  },
										  onComplete: function(response) {
												$("iceaccordion187").getElement(".iceaccordion-loading").setStyle("display","none");
												if(response !=""){
														current_page = nextpage; $("iceaccordion187").getElement(".iceaccordion").set("html", response);
														var myAccordion187 = new Accordion( $("iceaccordion187").getElement(".iceaccordion"), '#iceaccordion187 .toggler', '#iceaccordion187 .accordion_content', {
															opacity: false,
															alwaysHide:true,
															display: start_index187,
															onActive: function(toggler, element){
																toggler.addClass('open');
															},
															onBackground: function(toggler, element){
																toggler.removeClass('open');
															}
														});
														imageAccordionPreview( $("iceaccordion187") );
														$(blockHidden).set("html", $(blockHidden).get("html")+'
'+response+'');
																										}
												
											}
										}).send();
					}
				}
				function updateClassArrows( currentIndex ){
					var prev = $("iceaccordion187").getElement(".iceaccordion-prev");
					var next = $("iceaccordion187").getElement(".iceaccordion-next");
					if( currentIndex == 1){
						prev.addClass("disabled");
						next.removeClass("disabled");
					}
					else if( currentIndex == number_page ){
						prev.removeClass("disabled");
						next.addClass("disabled");
					}
					else{
						prev.removeClass("disabled");
						next.removeClass("disabled");
					}
					
				}
				function updateActiveClass( listpage, nextpage ){
					
					if( listpage[ nextpage ] ){
						listpage.removeClass("active");
						listpage[ nextpage ].addClass("active");
					}
					
				}
				
				imageAccordionPreview( $("iceaccordion187") );
			
				addPagingEvent187();
				
				$$("#iceaccordion187 .iceaccordion").each( function( item ){
						var myAccordion187 = new Accordion( item, '#iceaccordion187 .toggler', '#iceaccordion187 .accordion_content', {
							opacity: false,
							alwaysHide:true,
							display: start_index187,
							onActive: function(toggler, element){
								toggler.addClass('open');
							},
							onBackground: function(toggler, element){
								toggler.removeClass('open');
							}
						});
					} );
				});

