 window.addEvent('domready', function() {
    $$('.hasTip').each(function(el) {
      var title = el.get('title');
      if (title) {
        var parts = title.split('::', 2);
        el.store('tip:title', parts[0]);
        el.store('tip:text', parts[1]);
      }
    });
  var JTooltips = new Tips($$('.hasTip'), { fixed: false});
});

    window.addEvent('load', function() {
        if($('icepanel_wrapper218')==null){
            _lofmain = new Element('div', {'id': 'icepanel_wrapper218'});	
            _lofmain.set("html",$('icepanel218').get("html"));
            _lofmain.inject($(document.body));
        }
        else{
            _lofmain = $('icepanel_wrapper218');
        }
        $('icepanel218').dispose();
        var collapsible = new Fx.Slide(_lofmain.getElement('.icepanel'), {
            duration: 1000,
            transition: Fx.Transitions.Pow.easeInOut,
            direction: 'top',
            wrapper: 'icepanel_wrapper_218',
            mode: 'vertical'
        });
        _lofmain.getElement('.icepanel').setStyle('display','block');
                
        _lofmain.getElement(".trigger").addEvent("click",function(){ 
            collapsible.toggle();
            var parent_btn = _lofmain.getElement('.icepanel_button');
			var wrapper = $('panel_button_218');
            parent_btn.toggleClass('parent_active');
            parent_btn.toggleClass('parent_inactive');
			toggleTextByClass(wrapper, 'parent_active');
        });
        _lofmain.getElement(".btn_close_inside").addEvent("click",function(){
            var wrapper = $('panel_button_218');
            collapsible.toggle();
            wrapper.toggleClass('parent_active');
            wrapper.toggleClass('parent_inactive');
            toggleTextByClass(wrapper, 'parent_active');
        });                
    });
        
    function toggleTextByClass(elem, clsName) {
		var main_btn = elem.getChildren('a.trigger'); 
        if(elem.hasClass(clsName) == 'true' || elem.hasClass(clsName) == true){
            main_btn.set("html", 'Close');
        }else{
            main_btn.set("html", 'Visit Our Schools');
        }
        return false;        
    }
