	$(".db_background_url").click(function(){
		var url = $(this).data('db_background_url');
		if (url.indexOf('#') == 0 || url.indexOf('.') == 0) {
			et_pb_smooth_scroll($(url), false, 800);
		} else {
			document.location=url;
		}
	});
});

