    $( window ).resize( function(){
        if( $('#cboxOverlay' ).css( 'visibility' ) ){
        $(".group833288").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
    }
});
var $gallery833288 = $("a[rel=group833288]").colorbox({maxWidth: "80%"});
$("a#openGallery833288").click(function(e){
    e.preventDefault();
    $gallery833288.eq(0).click();
});

