    function rsMenu(schoolList) {
    	var pageURL = window.location.href.replace(/&/g, "%26");
        pageURL = encodeURIComponent(pageURL);

    	var mobileSchools = '';

        for(var i in schoolList) {
                  if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                      mobileSchools += '1,' + schoolList[i].schoolName + ',' + schoolList[i].schoolUrl + ', _blank;';
                  }
              }
        // RESPONSIVE MAIN MENU
        $("body").csRsMenu({
            "breakPoint"            : 640, // SYSTEM BREAK POINTS - 768, 640, 480, 320
            "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left   
            "primaryColor"          : "#2C363E", // DEFAULT - #2C363E
            "secondaryColor"        : "#222A30", // DEFAULT - #222A30
            "accentColor"           : "#B5BBBF", // DEFAULT - #B5BBBF
            "menuButtonParent"      : "#gb-mystart-left",
            "showMenuBtnText"       : "yes",
            "menuBtnText"           : "MENU",
            "showSchools"           : "no",
            "showTranslate"         : "no",
            "translateId"           : "none",
            "showAccount"           : "yes",
            "extraMenuOptions"      : "Schools; Social Media;Header Links", // EXTRA MENU HEADING NAMES SEPARATED BY A ;
            "extraMenuItems"        : mobileSchools+'1,Facebook,https://www.facebook.com/ElPasoISD,_blank;1,Twitter,https://twitter.com/ElPaso_ISD,_blank;1,YouTube,https://www.youtube.com/user/episdtvstudio,_blank;1,Instagram,https://www.instagram.com/episdschools/,_blank;1,LinkedIn,#,_blank;1,Vimeo,#,_blank;1,Flickr,#,_blank;1,Google+,#,_blank;1,Pinterest,#,_blank;2,Parents,/Page/760,_self;2,Students ,/Page/764,_self;2,Staff,https://my.episd.org/auth/,_blank;2,ESPAÑOL,//translate.google.com/translate?u='+pageURL+'&langpair=en|es&hl=en&anno=0&ie=UTF8,_self', // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
            "siteID"                : "4"
        });
    }
    function stickyHeader() {
        if($(window).width() >= 1280) { 
          scrolled = $(window).scrollTop();
          loc = $("#gb-header-outer").outerHeight();
          if(scrolled >= loc  && scrollLock == 0) {
              $("#gb-header-outer").hide().addClass("fixed-header");
              $("#gb-page").css("padding", loc+5+"px 0 0 0");
              $("#gb-header-outer").stop().fadeIn(500);
              scrollLock = 1;
          } else if(scrolled < loc && scrollLock == 1){
          	  $("#gb-page").css("padding", "0"); 
              $("#gb-header-outer").removeClass("fixed-header").hide().stop().fadeIn(500);
              scrollLock = 0;
          }
        }
	}

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { //Desktop View
			multimediaRotator();
		} else { //Mobile BPs
			multimediaRotator();
			appView();
		}
	}

	function multimediaRotator() {
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
			// MULTIMEDIA ROTATOR
			$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
						"imageWidth"              : 1500,
		                "imageHeight"             : 915,
		                "efficientLoad"           : "yes",
		                "appHeader"               : "no",
		                "titles"                  : "no",
		                "captions"                : "no",
		                "galleryOverlay"          : "no",
		                "linkedElement"           : ["image"], // ["image", "title", "overlay", "all"]
		                "playPause"               : "no",
		                "mainControls"            : "yes",
		                "contMoreLink"            : "no",
		                "contMoreLinkText"        : "Read More",
		                "descMoreLink"            : "no",
		                "descMoreLinkText"        : "Read More",
		                "bullets"                 : "no",
		                "bulletControls"          : "no",
		                "thumbnails"              : "no",
		                "thumbnailWidth"          : 122,
		                "thumbnailHeight"         : 53,
		                "thumbnailSlideDirection" : "horizontal", // horizontal or vertical
		                "hoverPause"              : "yes",
		                "autoRotate"              : "yes",
		                "fullScreenRotator"       : "yes", // image dimensions work well at 1500x915
		                "fullScreenBreakpoints"   : ["960"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
		                "fullScreenVideo"         : "no",
		                "transitionType"          : "fade", // fade or slide
		                "transitionSpeed"         : 3,
		                "transitionDelay"         : 8,
