            // ACCESSIBILITY         
            $(document).on("keydown", ".cs-mega-menu-section a", function(e) { 
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case _this.KeyCodes.left:
                    case _this.KeyCodes.up:
                        e.preventDefault();
                        if($(".cs-mega-menu-section a").index(this) == 0) {
                        	$(this).parents(".sw-channel-item").find("> a").focus();
                        } else {
                        	var index = $(".cs-mega-menu-section a").index(this)-1;
                        	$(".cs-mega-menu-section a").eq(index).focus();
                        }
                    break;             
                    // CONSUME RIGHT AND DOWN ARROWS
                    case _this.KeyCodes.right:
                    case _this.KeyCodes.down:
                        e.preventDefault();
                        if($(".cs-mega-menu-section a").index(this) == $(".cs-mega-menu-section a").length) {
                        	$(this).parents(".sw-channel-item").find("> a").focus();
                        } else {
                            var index = $(".cs-mega-menu-section a").index(this)+1;
                            $(".cs-mega-menu-section a").eq(index).focus();
                        }
                    break;
                    case _this.KeyCodes.tab:
                   		e.preventDefault();
                        //var index = $(".cs-mega-menu-section a").index(this)+1;
                        //$(".cs-mega-menu-section a").eq(index).focus();
                        $(this).parents(".sw-channel-item").find("> a").focus();
                    break;
                  }
            });

        },

        "mmDropdownHeight": function(){
        	$("#sw-channel-list-container li.sw-channel-item").each(function(){
            	var position = $(this).position();
                var positionTop = position.top;
                if(positionTop > 45){
                    $("ul.sw-channel-dropdown", this).css("top","100%");
                }
            });
        },

        "Body": function() {
            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

           //RENAME FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");

            //REPLACE MORE-LINK TEXT
            $("a.more-link span").text("View all");

            //REPLACE SUBSCRIBE TEXT
            $(".app-level-social-rss span").text("Subscribe");

            //AUTO-FOCUS UERSNAME SIGIN FIELD
            $("#swsignin-txt-username").focus();

            //CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
            if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
                $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
                //ADD SIGNED OUT BREADCRUMB
                $(".ui-breadcrumbs").append("
Signed Out");
            }

            //ADD SIGN IN BREADCRUMB
            if($("#swlogin").length){
                $("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
                $(".ui-breadcrumbs").append("
Sign In");
            }

                //REGISTER BREADCRUMB
            if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
                $("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
                $(".ui-breadcrumbs").append("
Register");
            }

            //SEARCH RESULTS BREADCRUMB
