        "SearchWidth": function() {
        	var headerWidth = $("#gb-header-inner").width();
            var windowWidth = $(window).width();
            var logoWidth = $("#gb-logo").width();
            var outsideWidth = (windowWidth - headerWidth)/2;
            var searchWidth = headerWidth - logoWidth + outsideWidth - 120;
            if(windowWidth > 480) {
            	$("#gb-search-container.open #sw-mystart-search").css("width",searchWidth);
            } else {
            	$("#gb-search-container.open #sw-mystart-search").css("width",windowWidth - 60);
            }
            $(window).resize(function(){
            	if(windowWidth > 480) {
                    $("#gb-search-container.open #sw-mystart-search").css("width",searchWidth);
                } else {
                    $("#gb-search-container.open #sw-mystart-search").css("width",windowWidth - 60);
                }
            });
        },


