        "SLM":  function() {
            var elemSchools = '', middleSchools = '', highSchools = '', alternativeSchools = '';

            for(var i in schoolList) {
                if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                    if(schoolList[i].schoolType.toLowerCase() == 'elementary' && schoolList[i].showFacebook.toLowerCase() =='true'&& schoolList[i].showTwitter.toLowerCase() =='true') {
                        elemSchools += '
' + schoolList[i].schoolName + '';
                    } 
                    else if(schoolList[i].schoolType.toLowerCase() == 'elementary' && schoolList[i].showFacebook.toLowerCase() =='true'&& schoolList[i].showTwitter.toLowerCase() =='false') {
                        elemSchools += '
' + schoolList[i].schoolName + '';
                    }
                    else if(schoolList[i].schoolType.toLowerCase() == 'elementary' && schoolList[i].showFacebook.toLowerCase() =='false'&& schoolList[i].showTwitter.toLowerCase() =='true') {
                        elemSchools += '
' + schoolList[i].schoolName + '';
                    }
                    else if(schoolList[i].schoolType.toLowerCase() == 'elementary' && schoolList[i].showFacebook.toLowerCase() =='false'&& schoolList[i].showTwitter.toLowerCase() =='false') {
                        elemSchools += '
' + schoolList[i].schoolName + '';
                    }
                    else if(schoolList[i].schoolType.toLowerCase() == 'middle' && schoolList[i].showFacebook.toLowerCase() =='true'&& schoolList[i].showTwitter.toLowerCase()  =='true') {
                        middleSchools += '
' + schoolList[i].schoolName + '';
                    }
                    else if(schoolList[i].schoolType.toLowerCase() == 'middle' && schoolList[i].showFacebook.toLowerCase() =='true'&& schoolList[i].showTwitter.toLowerCase() =='false') {
                        middleSchools += '
' + schoolList[i].schoolName + '';
                    }
                    else if(schoolList[i].schoolType.toLowerCase() == 'middle' && schoolList[i].showFacebook.toLowerCase() =='false'&& schoolList[i].showTwitter.toLowerCase() =='true') {
                        middleSchools += '
' + schoolList[i].schoolName + '';
                    }
                    else if(schoolList[i].schoolType.toLowerCase() == 'middle' && schoolList[i].showFacebook.toLowerCase() =='false'&& schoolList[i].showTwitter.toLowerCase() =='false') {
                        middleSchools += '
' + schoolList[i].schoolName + '';
                    }
                    else if(schoolList[i].schoolType.toLowerCase() == 'high' && schoolList[i].showFacebook.toLowerCase() =='true'&& schoolList[i].showTwitter.toLowerCase() =='true') {
                        highSchools += '
' + schoolList[i].schoolName + '';
                    }
                    else if(schoolList[i].schoolType.toLowerCase() == 'high' && schoolList[i].showFacebook.toLowerCase() =='true'&& schoolList[i].showTwitter.toLowerCase() =='false') {
                        highSchools += '
' + schoolList[i].schoolName + '';
                    }
                    else if(schoolList[i].schoolType.toLowerCase() == 'high' && schoolList[i].showFacebook.toLowerCase() =='false'&& schoolList[i].showTwitter.toLowerCase() =='true') {
                        highSchools += '
' + schoolList[i].schoolName + '';
                    }
                    else if(schoolList[i].schoolType.toLowerCase() == 'high' && schoolList[i].showFacebook.toLowerCase() =='false'&& schoolList[i].showTwitter.toLowerCase() =='false') {
                        highSchools += '
' + schoolList[i].schoolName + '';
                    }
                    else if(schoolList[i].schoolType.toLowerCase() == 'alternative' && schoolList[i].showFacebook.toLowerCase() =='true'&& schoolList[i].showTwitter.toLowerCase() =='true') {
                        alternativeSchools += '
' + schoolList[i].schoolName + '';
                    }
                    else if(schoolList[i].schoolType.toLowerCase() == 'alternative' && schoolList[i].showFacebook.toLowerCase() =='true'&& schoolList[i].showTwitter.toLowerCase() =='false') {
                        alternativeSchools += '
' + schoolList[i].schoolName + '';
                    }
                    else if(schoolList[i].schoolType.toLowerCase() == 'alternative' && schoolList[i].showFacebook.toLowerCase() =='false'&& schoolList[i].showTwitter.toLowerCase() =='true') {
                        alternativeSchools += '
' + schoolList[i].schoolName + '';
                    }
                    else if(schoolList[i].schoolType.toLowerCase() == 'alternative' && schoolList[i].showFacebook.toLowerCase() =='false'&& schoolList[i].showTwitter.toLowerCase() =='false') {
                        alternativeSchools += '
' + schoolList[i].schoolName + '';
                    }
                }
            }

            $('.gb-school-list-column.elem ul').append(elemSchools);
            $('.gb-school-list-column.middle ul').append(middleSchools);
            $('.gb-school-list-column.high ul').append(highSchools);
            $('.gb-school-list-column.alternative ul').append(alternativeSchools);

            // TAB HEADER CLICK
            $("#gb-school-list .gb-school-list-header").click(function(e){
                e.preventDefault();
                // IF THE SELECTED TAB ISNT ALREADY ACTIVE
                if(!$(this).hasClass("active")){
                    // GET CLASS OF SELECTED TAB
                    var selectedList = $(this).attr("class");
                    selectedList = jQuery.trim(selectedList.replace("gb-school-list-header ", ""));

                    //REMOVE CURRENT ACTIVE CLASSES
                    $("#gb-school-list .gb-school-list-header.active, #gb-school-list .gb-school-list-section.active").removeClass("active");


                    // SET NEW ACTIVE CLASSES
                    $(this).addClass("active");
                    $("#gb-school-list .gb-school-list-section." + selectedList).addClass("active");
                }


            });

            // SCHOOL TAB KEYDOWN
            $(".gb-school-list-header").keydown(function(e) {

                var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
                var lastIndex = $(".gb-school-list-header").length - 1;

                switch(event.keyCode){
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        event.preventDefault();

                        // FOCUS THE PREVIOUS TAB
                        if ($(this).index() == 0) { 
                            $(".gb-school-list-header:last-child").focus();
                        } else { 
                            $(".gb-school-list-header").eq($(this).index() - 1).focus();
                        } 
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        event.preventDefault();

                        // FOCUS THE NEXT TAB
                        if ($(this).index() == lastIndex) { 
                            $(".gb-school-list-header:first-child").focus();
                        } else { 
                            $(".gb-school-list-header").eq($(this).index() + 1).focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case csKeyCodes.home:
                        event.preventDefault();

                        // FOCUS THE FIRST TAB
                        $(".gb-school-list-header:first-child").focus();
                    break;

                    // CONSUME END KEY
                    case csKeyCodes.end:
                        event.preventDefault();

                        // FOCUS THE LAST TAB
                        $(".gb-school-list-header:last-child").focus();
                    break;
                }

            });

            // SCHOOL TAB FOCUS
            $(".gb-school-list-header").focus(function() {
                // ADJUST TAB ATTRIBUTES
                $(".gb-school-list-header").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
                $(this).attr({"tabindex": "0", "aria-selected": "true"}).addClass("active");

                // ADJUST PANEL ATTRIBUTES
                $(".gb-school-list-section").attr({"aria-hidden": "true"}).removeClass("active");
                $("#" + $(this).attr("aria-controls")).attr({"aria-hidden": "false"}).removeClass("active");
                $("#" + $(this).attr("aria-controls") + " li a" ).attr({"tabindex": "0"});
            });

            // SCHOOL TAB KEYDOWN
            $(".gb-school-list-section li a").keydown(function(e) {

                var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

                switch(event.keyCode){
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS LAST ITEM
                            $(this).closest("ul").find("li:last-child > a").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }

                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest("ul").find("li:first-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }  

                    break;

                    // CONSUME HOME KEY
                    case csKeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest("ul").find("li:first-child > a").focus();
                    break;

                    // CONSUME END KEY
                    case csKeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                    break;

                }

            });

            window.csTemplate.RsMenu(schoolList);

        },
