$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=CGo6npj4GtYBMrKUEEZTdQ%3d%3d&SessionID=e38ca18f-48c4-4287-bd78-5806a7ed2664&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f19%2f2020+4%3a20%3a10+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.78.10';
