			'icon6'        : ['/cms/lib/TX01918186/Centricity/Template/GlobalAssets/icons/menus_icon.png', 'Menus', '/Page/300', '_self'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			'icon7'        : ['/cms/lib/TX01918186/Centricity/template/globalassets/icons/bus-icon.png', 'Bus Routes', '/busroutes', '_blank'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			'icon8'        : ['Information Technology/1.png', 'Web Store', 'https://pearlandisd.revtrak.net/', '_self'], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
