		dynamicContent();

		if($(".hp").length) {
			$("body").csRsSameHeights({
				resizeElements : [
					{
						"resizeMethod"		: "columns",	// OPTIONS - columns, apps
						"parent"			: "#hp-content",	// PARENT THAT COLUMNS AND/OR APPS ARE IN THAT ARE GETTING RESIZED
						"columns"			: ".hp.column",	// COLUMNS AND/OR APPS WITHIN THESE COLUMNS THAT SAME HEIGHTS IS BEING APPLIED TO -- NEEDS TO BE A CLASS
						"gutter"			: 0,			// PIXEL SPACING BETWEEN APPS -- WILL BE ADDED AS BOTTOM MARGIN
						"applySameHeights"	: ["960"]	// OPTIONS - ["960", "768", "640", "480", "320"]
					}
				]
			});
			$("body").csRsSameHeights({
				resizeElements : [
					{
						"resizeMethod"		: "columns",	// OPTIONS - columns, apps
						"parent"			: "#slideshow-right",	// PARENT THAT COLUMNS AND/OR APPS ARE IN THAT ARE GETTING RESIZED
						"columns"			: ".ui-article",	// COLUMNS AND/OR APPS WITHIN THESE COLUMNS THAT SAME HEIGHTS IS BEING APPLIED TO -- NEEDS TO BE A CLASS
						"gutter"			: 0,			// PIXEL SPACING BETWEEN APPS -- WILL BE ADDED AS BOTTOM MARGIN
						"applySameHeights"	: ["640","480"]	// OPTIONS - ["960", "768", "640", "480", "320"]
					}
				]
			});
		}
		if($(".sp").length) {
			$("body").csRsSameHeights({
				resizeElements : [
					{
						"resizeMethod"		: "columns",	// OPTIONS - columns, apps
						"parent"			: "#sp-content",	// PARENT THAT COLUMNS AND/OR APPS ARE IN THAT ARE GETTING RESIZED
						"columns"			: ".sp.column",	// COLUMNS AND/OR APPS WITHIN THESE COLUMNS THAT SAME HEIGHTS IS BEING APPLIED TO -- NEEDS TO BE A CLASS
						"gutter"			: 0,			// PIXEL SPACING BETWEEN APPS -- WILL BE ADDED AS BOTTOM MARGIN
						"applySameHeights"	: ["960", "768"]	// OPTIONS - ["960", "768", "640", "480", "320"]
					}
				]
			});
		}


	});	

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			mediaRotator();
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			mediaRotator();
			$("#gb-global-icons").clone().removeClass("show960 hide768 show640").addClass("show768 hide640").appendTo("#hp-slideshow");
			if(!$(".hp.column.two #sw-content-container1").length) {
				$(".hp.column.one .column-inner.hide768").clone().removeClass("hide768").addClass("show768").prependTo(".hp.column.two");
			}
			if(!$(".hp.column.one #sw-content-container2").length) {
				$(".hp.column.two .column-inner.hide768").clone().removeClass("hide768").addClass("show768").prependTo(".hp.column.one");
			}
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			mediaRotator();
			if(!$(".hp.column.two #sw-content-container1").length) {
				$(".hp.column.one .column-inner.hide768").clone().removeClass("hide768").addClass("show768").prependTo(".hp.column.two");
			}
			if(!$(".hp.column.one #sw-content-container2").length) {
				$(".hp.column.two .column-inner.hide768").clone().removeClass("hide768").addClass("show768").prependTo(".hp.column.one");
			}
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			mediaRotator();
			if(!$(".hp.column.two #sw-content-container1").length) {
				$(".hp.column.one .column-inner.hide768").clone().removeClass("hide768").addClass("show768").prependTo(".hp.column.two");
			}
			if(!$(".hp.column.one #sw-content-container2").length) {
				$(".hp.column.two .column-inner.hide768").clone().removeClass("hide768").addClass("show768").prependTo(".hp.column.one");
			}
			appView();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			mediaRotator();
			if(!$(".hp.column.two #sw-content-container1").length) {
				$(".hp.column.one .column-inner.hide768").clone().removeClass("hide768").addClass("show768").prependTo(".hp.column.two");
			}
			if(!$(".hp.column.one #sw-content-container2").length) {
				$(".hp.column.two .column-inner.hide768").clone().removeClass("hide768").addClass("show768").prependTo(".hp.column.one");
			}
			appView();
		}
	}

