br.script:
function googleTranslateElementInit() {
    new google.translate.TranslateElement({
        pageLanguage: 'en',
        autoDisplay: false,
        layout: google.translate.TranslateElement.InlineLayout.SIMPLE
    }, 'google_translate_element');
}

function changeDisplay(sb)
{
    if(sb.substring(sb.length-1,sb.length)!=";")
	sb = sb + ";";
    
    var elm="";
    for(var i=0;i<sb.length;i++)
    {
	if(sb.substring(i,i+1)==";")
	{
	    if(elm.substring(0,1)=="i")
	    {
	    	if(document.getElementById(elm).src.substring(document.getElementById(elm).src.lastIndexOf("/")+1)=="tr-right.gif")
	    	    document.getElementById(elm).src="/SubFrames/images/Round-F4F4F9/tr-down.gif";
	        else
	    	    document.getElementById(elm).src="/SubFrames/images/Round-F4F4F9/tr-right.gif";
	    }
	    else if(elm.substring(0,1)=="m")
	    {
	        if(document.getElementById(elm).style.display=="none")
    	    	    document.getElementById(elm).style.display = "";
	        else
    	    	    document.getElementById(elm).style.display = "none";
	    }
	    elm = "";
	}
	else
	    elm = elm + sb.substring(i,i+1);
	
    }
}
