div.script:
$(document).ready(function () {
    $("#b1c33ffd-2888-4cfe-99c1-522d099dd11b").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 5 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#0667f86e-808b-4aa1-bb5a-3dab877f6c47").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#0667f86e-808b-4aa1-bb5a-3dab877f6c47').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#0667f86e-808b-4aa1-bb5a-3dab877f6c47').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 5000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 7 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
    jQuery('#cfb5fdcb-32da-487a-8753-ebfce7a42ce4').hide();
    jQuery('#cfb5fdcb-32da-487a-8753-ebfce7a42ce4').listSolrResults({
        currentPagePath: '/content/schools/utva/en',
        label: 'News',
        listFrom: 'solr',
        limit: '3',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/news.html',
        viewAllTitle: 'View All',
        uuid: 'cfb5fdcb-32da-487a-8753-ebfce7a42ce4',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

