function ENCrossURL() {
	var crossURL = "http://fhs.fcpsva.org";
	var d = document.location.host;
	var a = document.getElementsByTagName("a");
	for (i = 0; i < a.length; i++) {
		var h = a[i].href;
		if (h.indexOf(crossURL) == 0 && (h.indexOf(".jsp?") >= 0 || h.lastIndexOf(".jsp") == h.length - 4 || h.lastIndexOf("/") == h.length - 1 || h.substring(crossURL.length).indexOf(".") <= 0) && h.indexOf("_rss.jsp") < 0 && h.indexOf("/apps/events/ical.jsp") < 0 && h.indexOf("_pff.jsp") < 0 && h.indexOf("&pff=1") < 0 && h.indexOf("?pff=1") < 0) {
			var inner = a[i].innerHTML;

			var qm = h.indexOf("?");
			if (qm > 0) {
				var q = h.substring(qm);
				h = h.substring(0, qm) + escape(q);
			}

			a[i].href = "/apps/cross.jsp?wREC_ID=3639&crossPath=" + h.substring(crossURL.length);

			if (a[i].innerHTML != inner)
				a[i].innerHTML = inner;
		}
	}
}

ENCrossURL();

function playerReady(thePlayer) {
	player = window.document[thePlayer.id];
	try{
		player.addModelListener("STATE", "window.videoInterface.onPlayerStateChange");
		player.addModelListener("ERROR", "window.videoInterface.onPlayerPlaybackError");
		player.addModelListener("TIME", "window.videoInterface.onPlayerTimeChange");
		player.addModelListener("META", "window.videoInterface.onMeta");
		player.addControllerListener("SEEK", "window.videoInterface.onPlayerSeek");
	} catch (exp) {
		if(window.console)
			console.log(exp);
	}
}

