	function switchDip_380933(num,pause){
		clearTimeout(slideshowTimer_380933);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_380933();
		var speed = slideshowMotionSpeed_380933();
		var currentNum = document.getElementById('currentNum_380933').value;
		var totalDips = document.getElementById('totalDips_380933').value;
		var video = document.getElementById('video_380933').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_380933(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
