			}else{
				$('#dipImage_380933' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_380933' + nums).length){
				$('#videoShell_380933' + nums).fadeOut(speed);
			}
			if($('#dipContent_380933' + nums).length){
				$('#dipContent_380933' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_380933' + nums).length){
				$('#nav_380933' + nums).addClass('on');
			}
			
