$(document).ready(function() {
	if ($('.white-popup').length) {
		$('.slide-pop').magnificPopup({
		  disableOn: 700,
		  type: 'inline',
		  midClick: true,
		  mainClass: 'mfp-fade',
		  removalDelay: 160,
		  preloader: false,
		  fixedContentPos: false
		});
	}
	$('.main-slider').slick({
	  dots: true,
	  fade: true,
	  infinite: true,
	  adaptiveHeight: true, 
	  autoplay: true,
	  autoplaySpeed: '1000',
	  arrows: false,
	  appendDots: '.slideNav'
	});
});

	$('.annSlider').slick({
		slide:'.slide',
		autoplay: true,
	  	autoplaySpeed: '10000',
		centerPadding:'12.5%',
		dots:false,
		infinite:true,
		arrows:true,
		appendArrows:'.annArrow',
	    prevArrow:'Previous Slide',
		nextArrow:'Next Slide',
		centerMode: true,
		slidesToShow: 3,
	    slidesToScroll:1,
		cssEase: 'linear',
		variableWidth: true,
		variableHeight: true,
		responsive: [
		{
		  breakpoint: 1500,
		  settings: {
			centerPadding: '5%',
			slidesToShow: 3
		  }
		},
		{
		  breakpoint: 1024,
		  settings: {
			centerPadding: '5%',
			slidesToShow: 2
		  }
		},
		{
		  breakpoint: 980,
		  settings: {
			centerPadding: '2.5%',
			slidesToShow: 1
		  }
		}
		]
	});
