div.script:
function searchStaff(searchPage) {
    var lastName = jQuery("#staffLastName").val();
    var firstName = jQuery("#staffFirstName").val();
    var title = jQuery("#staffTitle").val();
    var groupName = jQuery("#staffGroup").val();
    var url = searchPage + "?searchtext=" +
        "&lastnamefilter=" + encodeURI(lastName) +
        "&firstnamefilter=" + encodeURI(firstName) +
        "&titlefilter=" + encodeURI(title) +
        "&buildingfilter=" + encodeURI(groupName);
    window.location = url;
    return false;
}

input.script:
	//
function isScrolledIntoView(elem)
{
    var docViewTop = jQuery(window).scrollTop();
    var docViewBottom = docViewTop + jQuery(window).height();

    var elemTop = jQuery(elem).offset().top;
    var elemBottom = elemTop + jQuery(elem).height();

    return (elemTop <= docViewBottom);
}
function initializeButton() {
  jQuery(".DownArrow").click(function() {
      jQuery('html, body').stop().animate({
	        'scrollTop': jQuery(jQuery("#content-wrapper .container")[0]).offset().top
	    }, 900, 'swing');
    });
    if(!isScrolledIntoView(jQuery("#content-wrapper .container")[0])) {
      jQuery(".DownArrow").show(1000);
      jQuery(window).resize(function(event) {
		if(isScrolledIntoView(jQuery("#content-wrapper .container")[0])) {
			jQuery(".DownArrow").hide(500);
            jQuery(this).off(event);
		}
      });
      jQuery(window).scroll(function(event) {
		if(isScrolledIntoView(jQuery("#content-wrapper .container")[0])) {
			jQuery(".DownArrow").hide(500);
            jQuery(this).off(event);
		}
      });
    }
  }
jQuery(document).ready(function() {
     jQuery(".DownArrow").hide();
     window.setTimeout(initializeButton, 1500);
});


