link.script:
$(document).ready(function() {
  var owl = $('#newsItem');
  owl.owlCarousel({
      navigation : true, // Show next and prev buttons
      slideSpeed : 300,
      paginationSpeed : 400,
      singleItem:true,
      autoHeight:true
  });

// for dots
 $('.owl-carousel .owl-controls .owl-page').attr('tabindex', 0).attr('role', "button");
 $('#newsItem .owl-page').on("keydown", function(e){
   var code = e.keyCode || e.which;
   if (code === 13) { // enter key
     n = $(this).index();
     owl.trigger('owl.goTo', n);
   }
 });
});

$(function () {
	if( $( '#emergencyAlert' ).length ) {
		$.magnificPopup.open({
			items: {
				src: $( '#emergencyAlert' ), // can be a HTML string, jQuery object, or CSS selector
				type: 'inline'
			}
		});
$(document).on('click', '.popup-modal-dismiss', function (e) {
	e.preventDefault();
	$.magnificPopup.close();
});
	}
});

var d = new Date();
	var eDate = new Array();
	var newsId;

	$('#emergencyAlert').each(function() {
		// get the end date data from each div
		eDate = new Date($( this ).data( 'end' ));
		// check if the end date is earlier or equal to today's date
		if( eDate < d ) {
			// it is earlier than today's date, so remove this div from the dom
			$( this ).remove();
		}
	});

