jQuery(window).on("resize", function() {
  resizeSocialBlocks();
}).resize();

function resizeSocialBlocks() {
  var win = $(this);
  var maxHeight = Math.max.apply(null, $(".text_home_page").map(function() {
    return $(this).height();
  }).get());
  jQuery('.text_home_page').css('min-height', maxHeight + 50);
}
